<script lang="tsx">
import useLazyLoad from '@/hook/useLazyLoad';
import { defineComponent, nextTick, ref } from 'vue';

export default defineComponent({
  props: {
    src: {
      type: String,
      default: 'data:image/png;base64,'
    },
    className: {
      type: String,
      default: ''
    },
    previewDisabled: {
      type: Boolean,
      default: true
    },
    fallbackSrc: {
      type: String,
      default: 'data:image/png;base64,'
    },
    loadingHeight: {
      type: String,
      default: '12vw'
    },
    loadingWidth: {
      type: String,
      default: '100%'
    },
    doubleClickPreview: {
      type: Boolean,
      default: true
    },
    borderRadius: {
      type: String,
      default: '6px'
    },
    hasHoverScale: {
      type: Boolean,
      default: true
    }
  },
  setup(props) {
    const isLoading = ref(true);
    const error = ref(false);
    let myPreviewDisabled = ref(props.doubleClickPreview && props.previewDisabled
      ? true
      : false);
    const handleLoad = () => {
      isLoading.value = false;
    };
    const handleError = () => {
      error.value = true;
    };
    //绑定双击事件函数
    const getClickPreviewMethod = () => {
      return myPreviewDisabled.value && {
        ondblclick: async (e: MouseEvent) => {
          myPreviewDisabled.value = false;
          await nextTick();
          (e.target as HTMLElement).click();
          setTimeout(() => {
            myPreviewDisabled.value = true;
          }, 1000);
        }
      };
    };
    let imageProps = {
      previewDisabled: props.previewDisabled,
      fallbackSrc: props.fallbackSrc,
      class: props.className
    };
    const { imageRef } = useLazyLoad(props.src);

    let imageClasses = props.className + 'w-full warpImg transition-all h-full duration-500 ';
    if (props.hasHoverScale) {
      imageClasses += 'group-hover-scale';
    }
    return () => {
      return (
        <div
          ref={imageRef} class={'group relative  h-full ' + props.className}
          {...getClickPreviewMethod()}
          style={{
            height: props.loadingHeight,
            zIndex: isLoading.value
              ? 10
              : error.value
                ? 999
                : 0
          }}>
          <n-image
            {...imageProps}
            class={imageClasses}
            on-load={handleLoad}
            src={props.src}
            on-error={handleError}
            preview-disabled={myPreviewDisabled.value}
            style={{
              opacity: isLoading.value
                ? 0
                : 1,
              borderRadius: props.borderRadius
            }} />
          <div
            v-show={isLoading.value}
            class='flex absolute top-0 left-0 justify-center items-center w-full h-full'
          >
            <n-skeleton class={props.className} loadingWidth={props.loadingWidth} height={props.loadingHeight} />
          </div>
        </div>
      );
    };
  }
});
</script>
<style scoped>
:deep(.warpImg > img) {
  width: 100%;
  height: 100%;
}
</style>
